import React, { useState,useContext } from 'react';

import Logo from '../../olx-logo.png';
import { FirebaseContext } from '../../store/Context'; 
import { useHistory } from 'react-router-dom';
import './Signup.css';

export default function Signup() {
  const history = useHistory()
  const [username,setUsername]= useState('')
  const [email,setEmail]= useState('')
  const [phone,setPhone]= useState('')
  const [password,setPassword]= useState('')
  const {firebase} = useContext(FirebaseContext)
  const [formError,serFormError]= useState({})

  const handleSubmit=(e)=>{
    e.preventDefault();
    const validate=(username,email,phone,password)=>{
      const errors = {};
      const regexMail = /"^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$"/i;
      const regexUsername = "^[A-Za-z][A-Za-z0-9_]{7,29}$";
      if(!username){
        errors.username= 'username is required';
      // }else if(!regexUsername.test(username)){
      //   errors.username="Username is not valid"
      }
      if(!email){
        errors.email="email is required"
  
      }else if(!regexMail.test(email)){
        errors.email="Not a valid email format"
      }
      if(!phone){
        errors.phone="phone number is required"
      }else if(phone.length!==10){
        errors.phone="Phone number must be 10 digit"
      }
      if(!password){
        errors.password="password is required"
      }else if(password.length<4){
        errors.password="Password must be more than 4 characters"
      }else if(password.length>10){
        errors.password="Password should not exceed 10 characters"
      }
      return errors
    }
    if(username.length<=4 || password.length<=6 || phone.length!=10 || email==='') {
      alert("Require all details")
    }else{
      firebase.auth().createUserWithEmailAndPassword(email,password).then((result)=>{
  
        result.user.updateProfile({displayName:username}).then(()=>{
          firebase.firestore().collection('users').add({
            id:result.user.uid,
            username:username,
            phone:phone
          }).then(()=>{
            history.push("/login")
          })
      
        })
      }).catch((error)=> {
        alert(error.message);
      })
    }
   

  }

  return (
    <div>
      <div className="signupParentDiv">
        <img width="200px" height="200px" src={Logo}></img>
        <form onSubmit={handleSubmit}>
          <label htmlFor="fname">Username</label>
          <br />
          <input
            className="input"
            type="text"
            value={username}
            onChange={(e)=>{
              setUsername(e.target.value)
            }}
            id="fname"
            name="name"
            defaultValue="John"
          />
          p
          <br />
          <label htmlFor="fname">Email</label>
          <br />
          <input
          value={email}
          onChange={(e)=>setEmail(e.target.value)}
            className="input"
            type="email"
            id="fname"
            name="email"
            defaultValue="John"
          />
          <br />
          <label htmlFor="lname">Phone</label>
          <br />
          <input
          value={phone}
          onChange={(e)=>setPhone(e.target.value)}
            className="input"
            type="number"
            id="lname"
            name="phone"
            defaultValue="Doe"
          />
          <br />
          <label htmlFor="lname">Password</label>
          <br />
          <input
          value={password}
          onChange={(e)=>setPassword(e.target.value)}
            className="input"
            type="password"
            id="lname"
            name="password"
            defaultValue="Doe"
          />
          <br />
          <br />
          <button>Signup</button>
        </form>
        <a onClick={()=>{
          history.push('/login')
        }}>Login</a>
      </div>
    </div>
  );
}
